import{_ as n,r as p,c as d,a as e,b as i,F as t,d as a,e as c,o as l}from"./app.958f2970.js";const r={},s=e("h1",{id:"_1-1-devops\u3001ci\u3001cd\u90FD\u662F\u4EC0\u4E48\u9B3C",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-1-devops\u3001ci\u3001cd\u90FD\u662F\u4EC0\u4E48\u9B3C","aria-hidden":"true"},"#"),a(" 1.1 DevOps\u3001CI\u3001CD\u90FD\u662F\u4EC0\u4E48\u9B3C\uFF1F")],-1),h=a("\u8F6C\u8F7D\u81EA\uFF1A"),g={href:"https://blog.jjonline.cn/linux/238.html",target:"_blank",rel:"noopener noreferrer"},m=a("DevOps\u3001CI\u3001CD\u90FD\u662F\u4EC0\u4E48\u9B3C\uFF1F| \u6676\u6676\u7684\u535A\u5BA2"),C=c('<p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122160637.jpg" alt="DevOps\u3001Ci\u3001Cd\u56FE" loading="lazy"></p><h2 id="devops" tabindex="-1"><a class="header-anchor" href="#devops" aria-hidden="true">#</a> DevOps</h2><p>DevOps\u662F<code>Development</code>\u548C<code>Operations</code>\u7684\u7EC4\u5408\uFF0C\u662F\u4E00\u79CD\u65B9\u6CD5\u8BBA\uFF0C\u662F\u4E00\u7EC4\u8FC7\u7A0B\u3001\u65B9\u6CD5\u4E0E\u7CFB\u7EDF\u7684\u7EDF\u79F0\uFF0C\u7528\u4E8E\u4FC3\u8FDB\u5E94\u7528\u5F00\u53D1\u3001\u5E94\u7528\u8FD0\u7EF4\u548C\u8D28\u91CF\u4FDD\u969C\uFF08QA\uFF09\u90E8\u95E8\u4E4B\u95F4\u7684\u6C9F\u901A\u3001\u534F\u4F5C\u4E0E\u6574\u5408\u3002\u4EE5\u671F\u6253\u7834\u4F20\u7EDF\u5F00\u53D1\u548C\u8FD0\u8425\u4E4B\u95F4\u7684\u58C1\u5792\u548C\u9E3F\u6C9F\u3002</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122165858.png" alt="\u5F00\u53D1\u548C\u8FD0\u8425\u4E4B\u95F4\u7684\u58C1\u5792\u548C\u9E3F\u6C9F" loading="lazy"></p><p>DevOps\u662F\u4E00\u79CD\u91CD\u89C6\u201C\u8F6F\u4EF6\u5F00\u53D1\u4EBA\u5458\uFF08Dev\uFF09\u201D\u548C\u201CIT\u8FD0\u7EF4\u6280\u672F\u4EBA\u5458\uFF08Ops\uFF09\u201D\u4E4B\u95F4\u6C9F\u901A\u5408\u4F5C\u7684\u6587\u5316\u3001\u8FD0\u52A8\u6216\u60EF\u4F8B\u3002\u901A\u8FC7\u81EA\u52A8\u5316\u201C\u8F6F\u4EF6\u4EA4\u4ED8\u201D\u548C\u201C\u67B6\u6784\u53D8\u66F4\u201D\u7684\u6D41\u7A0B\uFF0C\u6765\u4F7F\u5F97\u6784\u5EFA\u3001\u6D4B\u8BD5\u3001\u53D1\u5E03\u8F6F\u4EF6\u80FD\u591F\u66F4\u52A0\u5730\u5FEB\u6377\u3001\u9891\u7E41\u548C\u53EF\u9760\u3002\u5177\u4F53\u6765\u8BF4\uFF0C\u5C31\u662F\u5728\u8F6F\u4EF6\u4EA4\u4ED8\u548C\u90E8\u7F72\u8FC7\u7A0B\u4E2D\u63D0\u9AD8\u6C9F\u901A\u4E0E\u534F\u4F5C\u7684\u6548\u7387\uFF0C\u65E8\u5728\u66F4\u5FEB\u3001\u66F4\u53EF\u9760\u7684\u7684\u53D1\u5E03\u66F4\u9AD8\u8D28\u91CF\u7684\u4EA7\u54C1\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4DevOps\u662F\u4E00\u7EC4\u8FC7\u7A0B\u548C\u65B9\u6CD5\u7684\u7EDF\u79F0\uFF0C\u5E76\u4E0D\u6307\u4EE3\u67D0\u4E00\u7279\u5B9A\u7684\u8F6F\u4EF6\u5DE5\u5177\u6216\u8F6F\u4EF6\u5DE5\u5177\u7EC4\u5408\u3002\u5404\u79CD\u5DE5\u5177\u8F6F\u4EF6\u6216\u8F6F\u4EF6\u7EC4\u5408\u53EF\u4EE5\u5B9E\u73B0DevOps\u7684\u6982\u5FF5\u65B9\u6CD5\u3002\u5176\u672C\u8D28\u662F\u4E00\u6574\u5957\u7684\u65B9\u6CD5\u8BBA\uFF0C\u800C\u4E0D\u662F\u6307\u67D0\u79CD\u6216\u67D0\u4E9B\u5DE5\u5177\u96C6\u5408\uFF0C\u4E0E\u8F6F\u4EF6\u5F00\u53D1\u4E2D\u8BBE\u8BA1\u5230\u7684<code>OOP</code>\u3001<code>AOP</code>\u3001<code>IOC</code>\uFF08\u6216DI\uFF09\u7B49\u7C7B\u4F3C\uFF0C\u662F\u4E00\u79CD\u7406\u8BBA\u6216\u8FC7\u7A0B\u6216\u65B9\u6CD5\u7684\u62BD\u8C61\u6216\u4EE3\u79F0\u3002</p><h2 id="ci" tabindex="-1"><a class="header-anchor" href="#ci" aria-hidden="true">#</a> CI</h2><p>CI\u7684\u82F1\u6587\u540D\u79F0\u662F<code>Continuous Integration</code>\uFF0C\u4E2D\u6587\u7FFB\u8BD1\u4E3A\uFF1A\u6301\u7EED\u96C6\u6210\u3002</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122165932.png" alt="Continuous Integration" loading="lazy"></p><p>CI\u4E2D\uFF0C\u5F00\u53D1\u4EBA\u5458\u5C06\u4F1A\u9891\u7E41\u5730\u5411\u4E3B\u5E72\u63D0\u4EA4\u4EE3\u7801\uFF0C\u8FD9\u4E9B\u65B0\u63D0\u4EA4\u7684\u4EE3\u7801\u5728\u6700\u7EC8\u5408\u5E76\u5230\u4E3B\u5E72\u524D\uFF0C\u9700\u8981\u7ECF\u8FC7\u7F16\u8BD1\u548C\u81EA\u52A8\u5316\u6D4B\u8BD5\u6D41\u8FDB\u884C\u9A8C\u8BC1\u3002</p><p>\u6301\u7EED\u96C6\u6210\uFF08CI\uFF09\u662F\u5728\u6E90\u4EE3\u7801\u53D8\u66F4\u540E\u81EA\u52A8\u68C0\u6D4B\u3001\u62C9\u53D6\u3001\u6784\u5EFA\u548C\uFF08\u5728\u5927\u591A\u6570\u60C5\u51B5\u4E0B\uFF09\u8FDB\u884C\u5355\u5143\u6D4B\u8BD5\u7684\u8FC7\u7A0B\u3002\u6301\u7EED\u96C6\u6210\u7684\u76EE\u6807\u662F\u5FEB\u901F\u786E\u4FDD\u5F00\u53D1\u4EBA\u5458\u65B0\u63D0\u4EA4\u7684\u53D8\u66F4\u662F\u597D\u7684\uFF0C\u5E76\u4E14\u9002\u5408\u5728\u4EE3\u7801\u5E93\u4E2D\u8FDB\u4E00\u6B65\u4F7F\u7528\u3002CI\u7684\u6D41\u7A0B\u6267\u884C\u548C\u7406\u8BBA\u5B9E\u8DF5\u8BA9\u6211\u4EEC\u53EF\u4EE5\u786E\u5B9A\u65B0\u4EE3\u7801\u548C\u539F\u6709\u4EE3\u7801\u80FD\u5426\u6B63\u786E\u5730\u96C6\u6210\u5728\u4E00\u8D77\u3002</p><h2 id="cd" tabindex="-1"><a class="header-anchor" href="#cd" aria-hidden="true">#</a> CD</h2><p>CD\u53EF\u5BF9\u5E94\u591A\u4E2A\u82F1\u6587\u540D\u79F0\uFF0C\u6301\u7EED\u4EA4\u4ED8<code>Continuous Delivery</code>\u548C\u6301\u7EED\u90E8\u7F72<code>Continuous Deployment</code> \uFF0C\u4E00\u4E0B\u5206\u522B\u4ECB\u7ECD\u3002</p><p>\u67E5\u4E86\u4E00\u4E9B\u8D44\u6599\uFF0C\u5173\u4E8E\u6301\u7EED\u4EA4\u4E92\u548C\u6301\u7EED\u90E8\u7F72\u7684\u6982\u5FF5\u6BD4\u8F83\u6DF7\u4E71\uFF0C\u4EE5\u4E0B\u7684\u6982\u5FF5\u603B\u7ED3\u6309\u5927\u90E8\u5206\u7684\u8D44\u6599\u603B\u7ED3\u800C\u6765\u3002</p><h3 id="\u6301\u7EED\u4EA4\u4ED8" tabindex="-1"><a class="header-anchor" href="#\u6301\u7EED\u4EA4\u4ED8" aria-hidden="true">#</a> \u6301\u7EED\u4EA4\u4ED8</h3><p>\u5B8C\u6210 CI \u4E2D\u6784\u5EFA\u53CA\u5355\u5143\u6D4B\u8BD5\u548C\u96C6\u6210\u6D4B\u8BD5\u7684\u81EA\u52A8\u5316\u6D41\u7A0B\u540E\uFF0C\u6301\u7EED\u4EA4\u4ED8\u53EF\u81EA\u52A8\u5C06\u5DF2\u9A8C\u8BC1\u7684\u4EE3\u7801\u53D1\u5E03\u5230\u5B58\u50A8\u5E93\u3002\u4E3A\u4E86\u5B9E\u73B0\u9AD8\u6548\u7684\u6301\u7EED\u4EA4\u4ED8\u6D41\u7A0B\uFF0C\u52A1\u5FC5\u8981\u786E\u4FDD CI \u5DF2\u5185\u7F6E\u4E8E\u5F00\u53D1\u7BA1\u9053\u3002\u6301\u7EED\u4EA4\u4ED8\u7684\u76EE\u6807\u662F\u62E5\u6709\u4E00\u4E2A\u53EF\u968F\u65F6\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u7684\u4EE3\u7801\u5E93\u3002</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122170128.png" alt="\u6301\u7EED\u4EA4\u4ED8" loading="lazy"></p><p>\u5728\u6301\u7EED\u4EA4\u4ED8\u4E2D\uFF0C\u6BCF\u4E2A\u9636\u6BB5\uFF08\u4ECE\u4EE3\u7801\u66F4\u6539\u7684\u5408\u5E76\uFF0C\u5230\u751F\u4EA7\u5C31\u7EEA\u578B\u6784\u5EFA\u7248\u672C\u7684\u4EA4\u4ED8\uFF09\u90FD\u6D89\u53CA\u6D4B\u8BD5\u81EA\u52A8\u5316\u548C\u4EE3\u7801\u53D1\u5E03\u81EA\u52A8\u5316\u3002\u5728\u6D41\u7A0B\u7ED3\u675F\u65F6\uFF0C\u8FD0\u7EF4\u56E2\u961F\u53EF\u4EE5\u5FEB\u901F\u3001\u8F7B\u677E\u5730\u5C06\u5E94\u7528\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\u6216\u53D1\u5E03\u7ED9\u6700\u7EC8\u4F7F\u7528\u7684\u7528\u6237\u3002</p><h3 id="\u6301\u7EED\u90E8\u7F72" tabindex="-1"><a class="header-anchor" href="#\u6301\u7EED\u90E8\u7F72" aria-hidden="true">#</a> \u6301\u7EED\u90E8\u7F72</h3><p>\u5BF9\u4E8E\u4E00\u4E2A\u6210\u719F\u7684CI/CD\u7BA1\u9053\uFF08Pipeline\uFF09\u6765\u8BF4\uFF0C\u6700\u540E\u7684\u9636\u6BB5\u662F\u6301\u7EED\u90E8\u7F72\u3002\u4F5C\u4E3A\u6301\u7EED\u4EA4\u4ED8\u2014\u2014\u81EA\u52A8\u5C06\u751F\u4EA7\u5C31\u7EEA\u578B\u6784\u5EFA\u7248\u672C\u53D1\u5E03\u5230\u4EE3\u7801\u5B58\u50A8\u5E93\u2014\u2014\u7684\u5EF6\u4F38\uFF0C\u6301\u7EED\u90E8\u7F72\u53EF\u4EE5\u81EA\u52A8\u5C06\u5E94\u7528\u53D1\u5E03\u5230\u751F\u4EA7\u73AF\u5883\u3002</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122170247.png" alt="\u6301\u7EED\u90E8\u7F72" loading="lazy"></p><p>\u6301\u7EED\u90E8\u7F72\u610F\u5473\u7740\u6240\u6709\u7684\u53D8\u66F4\u90FD\u4F1A\u88AB\u81EA\u52A8\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\u3002\u6301\u7EED\u4EA4\u4ED8\u610F\u5473\u7740\u6240\u6709\u7684\u53D8\u66F4\u90FD\u53EF\u4EE5\u88AB\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\uFF0C\u4F46\u662F\u51FA\u4E8E\u4E1A\u52A1\u8003\u8651\uFF0C\u53EF\u4EE5\u9009\u62E9\u4E0D\u90E8\u7F72\u3002\u5982\u679C\u8981\u5B9E\u65BD\u6301\u7EED\u90E8\u7F72\uFF0C\u5FC5\u987B\u5148\u5B9E\u65BD\u6301\u7EED\u4EA4\u4ED8\u3002</p><p>\u6301\u7EED\u4EA4\u4ED8\u5E76\u4E0D\u662F\u6307\u8F6F\u4EF6\u6BCF\u4E00\u4E2A\u6539\u52A8\u90FD\u8981\u5C3D\u5FEB\u90E8\u7F72\u5230\u4EA7\u54C1\u73AF\u5883\u4E2D\uFF0C\u5B83\u6307\u7684\u662F\u4EFB\u4F55\u7684\u4EE3\u7801\u4FEE\u6539\u90FD\u53EF\u4EE5\u5728\u4EFB\u4F55\u65F6\u5019\u5B9E\u65BD\u90E8\u7F72\u3002</p><p>\u6301\u7EED\u4EA4\u4ED8\u8868\u793A\u7684\u662F\u4E00\u79CD\u80FD\u529B\uFF0C\u800C\u6301\u7EED\u90E8\u7F72\u8868\u793A\u7684\u5219\u4E00\u79CD\u65B9\u5F0F\u3002\u6301\u7EED\u90E8\u7F72\u662F\u6301\u7EED\u4EA4\u4ED8\u7684\u6700\u9AD8\u9636\u6BB5</p><h2 id="agile-development" tabindex="-1"><a class="header-anchor" href="#agile-development" aria-hidden="true">#</a> Agile Development</h2><p>\u53E6\u5916\u4E00\u4E2A\u6982\u5FF5\uFF0C\u4E5F\u5C31\u662F\u6240\u8C13\u7684\u654F\u6377\u5F00\u53D1\uFF0C\u4F3C\u4E4E\u8FD8\u6CA1\u6709\u6240\u8C13\u7684\u7B80\u79F0\uFF0C\u800C\u4E14\u8FD9\u4E2A\u79F0\u547C\u4F3C\u4E4E\u5728\u56FD\u5185\u88AB\u6EE5\u7528\u4E86\u3002\u654F\u6377\u5F00\u53D1\u7740\u91CD\u4E8E\u4E00\u79CD\u5F00\u53D1\u7684\u601D\u8DEF\uFF0C\u62E5\u62B1\u53D8\u5316\u548C\u5FEB\u901F\u8FED\u4EE3\u3002\u5982\u4F55\u5B9E\u73B0\u654F\u6377\u5F00\u53D1\uFF0C\u76EE\u524D\u4F3C\u4E4E\u5C1A\u6CA1\u6709\u5B8C\u5584\u7684\u5DE5\u5177\u94FE\uFF0C\u66F4\u591A\u7684\u662F\u4E00\u79CD\u6982\u5FF5\u6027\uFF0C\u8C03\u4F83\u7684\u8BF4\u6CD5\u201C\u65E2\u60F3\u9A6C\u5C14\u8DD1\u5F97\u5FEB\uFF0C\u53C8\u60F3\u9A6C\u513F\u4E0D\u5403\u8349\u201D\u7684\u53E6\u5916\u4E00\u79CD\u8BF4\u6CD5\u3002</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122170723.png" alt="Agile Development" loading="lazy"></p><p>\u4E0A\u56FE\u63ED\u793A\u4E86\u654F\u6377\u5F00\u53D1\u7684\u4E00\u4E9B\u5185\u6DB5\u548C\u76EE\u6807\uFF0C\u4F3C\u4E4E\u6709\u70B9\u513F\u4E00\u672C\u771F\u7ECF\u7684\u80E1\u8BF4\u516B\u9053\u7684\u610F\u601D\u3002</p><h2 id="ci\u3001cd\u3001devops\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#ci\u3001cd\u3001devops\u5173\u7CFB" aria-hidden="true">#</a> CI\u3001CD\u3001DevOps\u5173\u7CFB</h2><p>\u6982\u5FF5\u6027\u7684\u5185\u5BB9\uFF0C\u6BCF\u4E2A\u4EBA\u7684\u7406\u89E3\u90FD\u6709\u6240\u4E0D\u540C\u3002\u5C31\u597D\u6BD4<code>CGI</code> \u8FD9\u4E2A\u8BCD\uFF0C\u5373\u53EF\u4EE5\u7406\u89E3\u6210CGI\u8FD9\u79CD\u534F\u8BAE\uFF0C\u4E5F\u53EF\u4EE5\u7406\u89E3\u6210\u5B9E\u73B0\u4E86CGI\u534F\u8BAE\u7684\u8F6F\u4EF6\u5DE5\u5177\uFF0C\u90FD\u6CA1\u6709\u95EE\u9898\uFF0C\u54AC\u6587\u56BC\u5B57\u8FC7\u72B9\u4E0D\u53CA\u3002\u7559\u4E0B\u4E00\u56FE\uFF1A</p><p><img src="https://blog.jjonline.cn/Upload/image/201901/20190122160600.png" alt="DevOps\u3001CI\u3001CD\u7684\u533A\u522B" loading="lazy"></p>',31);function _(v,u){const o=p("ExternalLinkIcon");return l(),d(t,null,[s,e("blockquote",null,[e("p",null,[h,e("a",g,[m,i(o)])])]),C],64)}var I=n(r,[["render",_],["__file","cicd.html.vue"]]);export{I as default};
