import{_ as i,r,c as t,a as l,b as n,F as o,e as s,d as a,o as h}from"./app.e34c9e1b.js";const c={},d=s('<h2 id="cni-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#cni-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> CNI \u662F\u4EC0\u4E48</h2><p>\u9996\u5148\u6211\u4EEC\u4ECB\u7ECD\u4E00\u4E0B\u4EC0\u4E48\u662F CNI\uFF0C\u5B83\u7684\u5168\u79F0\u662F Container Network Interface\uFF0C\u5373\u5BB9\u5668\u7F51\u7EDC\u7684 API \u63A5\u53E3\u3002</p><p>\u5B83\u662F K8s \u4E2D\u6807\u51C6\u7684\u4E00\u4E2A\u8C03\u7528\u7F51\u7EDC\u5B9E\u73B0\u7684\u63A5\u53E3\u3002Kubelet \u901A\u8FC7\u8FD9\u4E2A\u6807\u51C6\u7684 API \u6765\u8C03\u7528\u4E0D\u540C\u7684\u7F51\u7EDC\u63D2\u4EF6\u4EE5\u5B9E\u73B0\u4E0D\u540C\u7684\u7F51\u7EDC\u914D\u7F6E\u65B9\u5F0F\uFF0C\u5B9E\u73B0\u4E86\u8FD9\u4E2A\u63A5\u53E3\u7684\u5C31\u662F CNI \u63D2\u4EF6\uFF0C\u5B83\u5B9E\u73B0\u4E86\u4E00\u7CFB\u5217\u7684 CNI API \u63A5\u53E3\u3002\u5E38\u89C1\u7684 CNI \u63D2\u4EF6\u5305\u62EC Calico\u3001flannel\u3001Terway\u3001Weave Net \u4EE5\u53CA Contiv\u3002</p><h2 id="\u54EA\u4E2A-cni-\u63D2\u4EF6\u9002\u5408\u6211" tabindex="-1"><a class="header-anchor" href="#\u54EA\u4E2A-cni-\u63D2\u4EF6\u9002\u5408\u6211" aria-hidden="true">#</a> \u54EA\u4E2A CNI \u63D2\u4EF6\u9002\u5408\u6211</h2><p>\u793E\u533A\u6709\u5F88\u591A\u7684 CNI \u63D2\u4EF6\uFF0C\u6BD4\u5982 Calico, flannel, Terway \u7B49\u7B49\u3002\u90A3\u4E48\u5728\u4E00\u4E2A\u771F\u6B63\u5177\u4F53\u7684\u751F\u4EA7\u73AF\u5883\u4E2D\uFF0C\u6211\u4EEC\u8981\u9009\u62E9\u54EA\u4E00\u4E2A CNI \u63D2\u4EF6\u5462\uFF1F</p><p>\u8FD9\u5C31\u8981\u4ECE CNI \u7684\u51E0\u79CD\u5B9E\u73B0\u6A21\u5F0F\u8BF4\u8D77\u3002\u6211\u4EEC\u9700\u8981\u6839\u636E\u4E0D\u540C\u7684\u573A\u666F\u9009\u62E9\u4E0D\u540C\u7684\u5B9E\u73B0\u6A21\u5F0F\uFF0C\u518D\u53BB\u9009\u62E9\u5BF9\u5E94\u7684\u5177\u4F53\u67D0\u4E00\u4E2A\u63D2\u4EF6\u3002</p><p>\u901A\u5E38\u6765\u8BF4\uFF0CCNI \u63D2\u4EF6\u53EF\u4EE5\u5206\u4E3A\u4E09\u79CD\uFF1AOverlay\u3001\u8DEF\u7531\u53CA Underlay\u3002</p><h2 id="\u5E38\u7528\u7F51\u7EDC\u672F\u8BED" tabindex="-1"><a class="header-anchor" href="#\u5E38\u7528\u7F51\u7EDC\u672F\u8BED" aria-hidden="true">#</a> <strong>\u5E38\u7528\u7F51\u7EDC\u672F\u8BED</strong></h2><p><strong>2\u5C42\u7F51\u7EDC</strong></p><ul><li>OSI \u7F51\u7EDC\u6A21\u578B\u4E2D\u7684\u6570\u636E\u94FE\u8DEF\u5C42.</li><li>\u5904\u7406\u7F51\u7EDC\u4E0A\u4E24\u4E2A\u76F8\u90BB\u8282\u70B9\u4E4B\u95F4\u7684\u5E27\u4F20\u9012.</li><li>\u4EE5\u592A\u7F51\u5C31\u5DE5\u4F5C\u5728\u7B2C2\u5C42, MAC\u5730\u5740\u8868\u793A\u4E3A\u5B50\u5C42.</li></ul><p><strong>3\u5C42\u7F51\u7EDC</strong></p><ul><li>OSI \u7F51\u7EDC\u6A21\u578B\u4E2D\u7684\u7F51\u7EDC\u5C42.</li><li>\u5904\u7406\u4E3B\u673A\u4E4B\u95F4\u8DEF\u7531\u6570\u636E\u5305.</li><li>IPv4\u3001IPv6\u3001ICMP \u5DE5\u4F5C\u5728\u7B2C3\u5C42.</li></ul><p><strong>VXLAN</strong></p><ul><li>VXLAN\u4EE3\u8868\u865A\u62DF\u53EF\u6269\u5C55\u7684LAN.</li><li>VXLAN\u662F\u4E00\u79CD\u5C01\u88C5\u548C\u8986\u76D6\u534F\u8BAE, \u53EF\u5728\u73B0\u6709\u7F51\u7EDC\u4E0A\u8FD0\u884C.</li><li>VXLAN\u865A\u62DF\u5316\u4E0EVLAN\u7C7B\u4F3C, \u4F46\u63D0\u4F9B\u66F4\u5927\u7684\u7075\u6D3B\u6027\u548C\u529F\u80FD, VLAN\u53EA\u67094096\u4E2A\u7F51\u7EDCID.</li><li>VXLAN\u7528\u4E8E\u901A\u8FC7\u5728UDP\u6570\u636E\u62A5\u4E2D\u5C01\u88C5\u7B2C2\u5C42\u4EE5\u592A\u7F51\u5E27\u6765\u5B9E\u73B0\u5927\u578B\u7F51\u7EDC\u90E8\u7F72.</li></ul><p><strong>Overlay</strong></p><ul><li>Overlay\u7F51\u7EDC\u662F\u5EFA\u7ACB\u5728\u73B0\u6709\u7F51\u7EDC\u4E4B\u4E0A\u7684\u865A\u62DF\u903B\u8F91\u7F51\u7EDC.</li><li>Overlay\u7F51\u7EDC\u901A\u5E38\u7528\u4E8E\u5728\u73B0\u6709\u7F51\u7EDC\u4E4B\u4E0A\u63D0\u4F9B\u6709\u7528\u7684\u62BD\u8C61, \u5E76\u5206\u79BB\u548C\u4FDD\u62A4\u4E0D\u540C\u7684\u903B\u8F91\u7F51\u7EDC.</li></ul><p><strong>\u6570\u636E\u5C01\u88C5</strong></p><ul><li>\u6570\u636E\u5C01\u88C5\u662F\u6307\u5728\u9644\u52A0\u5C42\u4E2D\u5C01\u88C5\u7F51\u7EDC\u6570\u636E\u5305\u4EE5\u63D0\u4F9B\u5176\u4ED6\u4E0A\u4E0B\u6587\u548C\u4FE1\u606F\u7684\u8FC7\u7A0B.</li><li>\u5728Overlay\u7F51\u7EDC\u4E2D, \u6570\u636E\u5C01\u88C5\u88AB\u7528\u4E8E\u4ECE\u865A\u62DF\u7F51\u7EDC\u8F6C\u6362\u5230\u5E95\u5C42\u5730\u5740\u7A7A\u95F4, \u4ECE\u800C\u80FD\u8DEF\u7531\u5230\u4E0D\u540C\u7684\u4F4D\u7F6E.</li></ul><p><strong>\u7F51\u72B6\u7F51\u7EDC(Mesh NetWork)</strong></p><ul><li>\u7F51\u72B6\u7F51\u7EDC\u662F\u6307\u6BCF\u4E2A\u8282\u70B9\u8FDE\u63A5\u5230\u8BB8\u591A\u5176\u4ED6\u8282\u70B9\u4EE5\u534F\u4F5C\u8DEF\u7531\u3001\u5E76\u5B9E\u73B0\u66F4\u5927\u8FDE\u63A5\u7684\u7F51\u7EDC.</li><li>\u7F51\u72B6\u7F51\u7EDC\u5141\u8BB8\u901A\u8FC7\u591A\u4E2A\u8DEF\u5F84\u8FDB\u884C\u8DEF\u7531, \u4ECE\u800C\u63D0\u4F9B\u66F4\u53EF\u9760\u7684\u7F51\u7EDC.</li><li>\u7F51\u72B6\u7F51\u7EDC\u7684\u7F3A\u70B9\u662F\u6BCF\u4E2A\u9644\u52A0\u8282\u70B9\u90FD\u4F1A\u589E\u52A0\u5927\u91CF\u5F00\u9500.</li></ul><p><strong>BGP</strong></p><ul><li>BGP\u4EE3\u8868\u201D\u8FB9\u754C\u7F51\u5173\u534F\u8BAE\u201D, \u7528\u4E8E\u7BA1\u7406\u8FB9\u7F18\u8DEF\u7531\u5668\u4E4B\u95F4\u6570\u636E\u5305\u7684\u8DEF\u7531\u65B9\u5F0F.</li><li>BGP\u901A\u8FC7\u8003\u8651\u53EF\u7528\u8DEF\u5F84, \u8DEF\u7531\u89C4\u5219\u548C\u7279\u5B9A\u7F51\u7EDC\u7B56\u7565, \u5E2E\u52A9\u5F04\u6E05\u695A\u5982\u4F55\u5C06\u6570\u636E\u5305\u4ECE\u4E00\u4E2A\u7F51\u7EDC\u53D1\u9001\u5230\u53E6\u4E00\u4E2A\u7F51\u7EDC.</li><li>BGP\u6709\u65F6\u88AB\u7528\u4F5CCNI\u63D2\u4EF6\u4E2D\u7684\u8DEF\u7531\u673A\u5236, \u800C\u4E0D\u662F\u5C01\u88C5\u7684\u8986\u76D6\u7F51\u7EDC.</li></ul><h2 id="flannel" tabindex="-1"><a class="header-anchor" href="#flannel" aria-hidden="true">#</a> Flannel</h2><p>Flannel \u7684\u6846\u67B6\u5305\u542B\u4EE5\u4E0B\u7EC4\u4EF6\uFF1A\u6BCF\u4E2A\u8282\u70B9\u4E0A\u7684\u4EE3\u7406\u670D\u52A1 flanneld\uFF0C\u8D1F\u8D23\u4E3A\u6BCF\u4E2A\u4E3B\u673A\u5206\u914D\u548C\u7BA1\u7406\u5B50\u7F51\uFF1B\u5168\u5C40\u7684\u7F51\u7EDC\u914D f \u7F6E\u5B58\u50A8 etcd\uFF08\u6216 K8S API\uFF09\u8D1F\u8D23\u5B58\u50A8\u4E3B\u673A\u548C\u5BB9\u5668\u5B50\u7F51\u7684\u6620\u5C04\u5173\u7CFB\uFF1B\u591A\u79CD\u7F51\u7EDC\u8F6C\u53D1\u529F\u80FD\u7684\u540E\u7AEF\u5B9E\u73B0\u3002\u672C\u6587\u4E3B\u8981\u4ECB\u7ECD\u4E09\u79CD\u6700\u5E38\u89C1\u7684\u6A21\u5F0F\uFF1AUDP\u3001VXLAN \u548C Host-gateway\uFF08\u4EE5\u4E0B\u7B80\u79F0 host-gw\uFF09\u3002</p><ol><li>udp: \u4F7F\u7528\u7528\u6237\u6001udp\u5C01\u88C5,\u9ED8\u8BA4\u4F7F\u75288285\u7AEF\u53E3, \u7531\u4E8E\u662F\u5728\u7528\u6237\u6001 \u5C01\u5305\u4E0E\u89E3\u5305, \u6027\u80FD\u4E0A\u6709\u8F83\u5927\u7684\u635F\u8017.</li><li>vxlan: vxlan\u5C01\u88C5, \u9700\u8981\u914D\u7F6EVNI, Port ( \u9ED8\u8BA4\u7AEF\u53E38472 ) \u548C BGP.</li><li>host-gw: \u76F4\u63A5\u8DEF\u7531\u6A21\u5F0F, \u5C06\u5BB9\u5668\u7F51\u7EDC\u7684\u8DEF\u7531\u4FE1\u606F\u76F4\u63A5\u66F4\u65B0\u5230\u4E3B\u673A\u7684\u8DEF\u7531\u8868\u4E2D. \u4EC5\u9002\u7528\u4E8E\u4E8C\u5C42\u76F4\u63A5\u53EF\u8FBE\u7684\u7F51\u7EDC\u4E2D, \u63A8\u8350\u5728\u7F51\u7EDC\u81EA\u7531\u7684\u73AF\u5883\u4E2D\u4F7F\u7528, \u6548\u7387\u8F83\u9AD8.</li></ol><h3 id="flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-udp" tabindex="-1"><a class="header-anchor" href="#flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-udp" aria-hidden="true">#</a> Flannel \u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B UDP</h3><p>UDP \u662F\u4E0E Docker \u7F51\u6865\u6A21\u5F0F\u6700\u76F8\u4F3C\u7684\u5B9E\u73B0\u6A21\u5F0F\u3002\u4E0D\u540C\u7684\u662F\uFF0C<strong>UDP \u6A21\u5F0F\u5728\u865A\u62DF\u7F51\u6865\u57FA\u7840\u4E0A\u5F15\u5165\u4E86 TUN \u8BBE\u5907\uFF08flannel0\uFF09</strong>\u3002TUN \u8BBE\u5907\u7684\u7279\u6B8A\u6027\u5728\u4E8E\u5B83\u53EF\u4EE5\u628A\u6570\u636E\u5305\u8F6C\u7ED9\u521B\u5EFA\u5B83\u7684\u7528\u6237\u7A7A\u95F4\u8FDB\u7A0B\uFF0C\u4ECE\u800C\u5B9E\u73B0\u5185\u6838\u5230\u7528\u6237\u7A7A\u95F4\u7684\u62F7\u8D1D\u3002<strong>\u5728 Flannel \u4E2D\uFF0Cflannel0 \u7531 flanneld \u8FDB\u7A0B\u521B\u5EFA\uFF0C\u56E0\u6B64\u4F1A\u628A\u5BB9\u5668\u7684\u6570\u636E\u5305\u8F6C\u5230 flanneld\uFF0C\u7136\u540E\u7531 flanneld \u5C01\u5305\u8F6C\u7ED9\u5BBF\u4E3B\u673A\u53D1\u5411\u5916\u90E8\u7F51\u7EDC</strong>\u3002</p><p><strong>flannel0 \u662F\u5185\u6838\u6001\uFF0Cflanneld \u662F\u7528\u6237\u6001\uFF0C\u6700\u7EC8\u53C8\u8981\u901A\u8FC7\u5185\u6838\u5C06\u6570\u636E\u53D1\u5230\u5916\u90E8\u7F51\u7EDC\uFF0C\u56E0\u6B64\u6027\u80FD\u635F\u8017\u8F83\u5927</strong>\uFF0C\u5BF9\u4E8E\u6709\u6570\u636E\u4F20\u8F93\u6709\u8981\u6C42\u7684\u5728\u7EBF\u4E1A\u52A1\u5E76\u4E0D\u9002\u7528\u3002</p><h3 id="flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-vxlan" tabindex="-1"><a class="header-anchor" href="#flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-vxlan" aria-hidden="true">#</a> Flannel \u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B VXLAN</h3><ul><li>VXLAN \u6A21\u5F0F\u4F7F\u7528\u6BD4\u8F83\u7B80\u5355, flannel \u4F1A\u5728\u5404\u8282\u70B9\u751F\u6210\u4E00\u4E2A flannel.1 \u7684 VXLAN \u7F51\u5361(VTEP\u8BBE\u5907).</li><li>VXLAN \u6A21\u5F0F\u4E0B\u5C01\u5305\u4E0E\u89E3\u5305\u7684\u5DE5\u4F5C\u662F\u7531\u5185\u6838\u8FDB\u884C\u7684. flannel\u4E0D\u8F6C\u53D1\u6570\u636E, \u4EC5\u52A8\u6001\u8BBE\u7F6EARP\u548CFDB\u8868\u9879.</li><li>VXLAN \u6A21\u5F0F\u4E0B\u901A\u4FE1\u5982\u4E0B(\u8DE8\u4E3B\u673A\u7684\u60C5\u51B5)</li></ul><p><img src="https://gitee.com/clay-wangzhi/blogImg/raw/master/blogImg/v2-5ef222f540110bd58a3ea5e77015bd29_720w.jpg" alt="img" loading="lazy"></p><ol><li>\u5BB9\u5668A \u4E2D\u7684\u6570\u636E\u5305\u5148\u901A\u8FC7\u5BB9\u5668A \u7684\u8DEF\u7531\u8868\u53D1\u9001\u5230 cni0.</li><li>cni0 \u901A\u8FC7\u5339\u914D\u4E3B\u673AA\u4E2D\u7684\u8DEF\u7531\u8868\u4FE1\u606F,\u5C06\u6570\u636E\u5305\u53D1\u9001\u5230 flannel.1 \u63A5\u53E3.</li><li>flannel.1 \u662F\u4E00\u4E2A VTEP \u8BBE\u5907, \u6536\u5230\u62A5\u6587\u540E\u6309\u7167 VTEP\u7684\u914D\u7F6E\u8FDB\u884C\u5C01\u5305. \u6839\u636Eflannel.1\u8BBE\u5907\u521B\u5EFA\u65F6\u8BBE\u7F6E\u7684\u53C2\u6570 VNI\u3001local IP\u3001Port \u8FDB\u884CVXLAN\u5C01\u5305\u3002</li><li>\u4E3B\u673AA\u901A\u8FC7\u7269\u7406\u7F51\u5361 eth0 \u53D1\u9001\u5C01\u5305\u5230 \u4E3B\u673AB\u7684\u7269\u7406\u7F51\u5361 eth0\u4E2D.</li><li>\u4E3B\u673AB\u7684\u7269\u7406\u7F51\u5361eth0 \u518D\u901A\u8FC7VXLAN\u9ED8\u8BA4\u7AEF\u53E38472 \u8F6C\u53D1\u5230 VTEP \u8BBE\u5907flannel.1 \u8FDB\u884C\u89E3\u5305.</li><li>\u89E3\u5305\u4EE5\u540E\u6839\u636EIP\u5934\u5339\u914D\u8DEF\u7531\u89C4\u5219, \u5185\u6838\u5C06\u5305\u53D1\u9001\u5230cni0.</li><li>cni0 \u53D1\u9001\u5230\u6865\u63A5\u5230\u6B64\u63A5\u53E3\u7684\u5BB9\u5668B\u4E2D.</li></ol><p>\u4F5C\u4E3A Flannel \u4E2D\u6700\u88AB\u666E\u904D\u91C7\u7528\u7684\u65B9\u6848\uFF0CVXLAN \u91C7\u7528\u7684\u662F\u5185\u7F6E\u5728 Linux \u5185\u6838\u91CC\u7684\u6807\u51C6\u534F\u8BAE\uFF0C\u56E0\u6B64\u867D\u7136\u5C01\u5305\u7ED3\u6784\u6BD4 UDP \u6A21\u5F0F\u590D\u6742\uFF0C\u4F46\u88C5\u5305\u548C\u89E3\u5305\u8FC7\u7A0B\u5747\u5728\u5185\u6838\u4E2D\u5B8C\u6210\uFF0C\u5B9E\u9645\u7684\u4F20\u8F93\u901F\u5EA6\u8981\u6BD4 UDP \u6A21\u5F0F\u5FEB\u8BB8\u591A\u3002\u8F83\u5FEB\u7684\u4F20\u8F93\u901F\u5EA6\u548C\u5BF9\u5E95\u5C42\u7F51\u7EDC\u7684\u53EF\u517C\u5BB9\u6027\u4E5F\u4F7F\u5F97 VXLAN \u9002\u7528\u6027\u8F83\u5176\u4ED6\u6A21\u5F0F\u66F4\u9AD8\uFF0C\u6210\u4E3A\u4E1A\u52A1\u73AF\u5883\u4E0B\u7684\u4E3B\u6D41\u9009\u62E9\u3002</p><h3 id="flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-host-gw" tabindex="-1"><a class="header-anchor" href="#flannel-\u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B-host-gw" aria-hidden="true">#</a> Flannel \u6570\u636E\u8F6C\u53D1\u6A21\u5F0F\u4E4B Host-gw</h3><p>\u9664\u53BB\u4E0A\u8FF0\u4E24\u79CD\u6A21\u5F0F\u5916\uFF0CFlannel \u8FD8\u63D0\u4F9B\u4E86\u4E00\u79CD\u7EAF\u4E09\u5C42\u7F51\u7EDC\u6A21\u5F0F host-gw\u3002\u987E\u540D\u601D\u4E49\uFF0Chost-gw \u662F\u4E00\u79CD\u4E3B\u673A\u7F51\u5173\u6A21\u5F0F\uFF0C\u6BCF\u4E2A\u4E3B\u673A\u4F1A\u7EF4\u62A4\u4E00\u5F20\u8DEF\u7531\u8868\uFF0C\u8BB0\u5F55\u53D1\u5F80\u67D0\u76EE\u6807\u5BB9\u5668\u5B50\u7F51\u7684\u6570\u636E\u5305\u7684\u4E0B\u4E00\u8DF3 IP \u5730\u5740\uFF08\u4E5F\u5C31\u662F\u5B50\u7F51\u6240\u5728\u5BBF\u4E3B\u673A\u7684 IP\uFF09\u3002\u5BBF\u4E3B\u673A\u5C06\u4E0B\u4E00\u8DF3\u76EE\u7684\u4E3B\u673A\u7684 MAC \u5730\u5740\u4F5C\u4E3A\u76EE\u7684\u5730\u5740\uFF0C\u901A\u8FC7\u4E8C\u5C42\u7F51\u7EDC\u628A\u5305\u53D1\u5F80\u76EE\u7684\u4E3B\u673A\u3002\u76EE\u7684\u4E3B\u673A\u6536\u5230\u540E\uFF0C\u4F1A\u76F4\u63A5\u8F6C\u53D1\u7ED9\u5BF9\u5E94\u5BB9\u5668\u3002\u6240\u4EE5 host-gw \u6A21\u5F0F\u4E0B\uFF0C\u6570\u636E\u5305\u76F4\u63A5\u4EE5\u5BB9\u5668 IP \u5305\u7684\u5F62\u5F0F\u5728\u7F51\u7EDC\u4E2D\u4F20\u9012\uFF0C\u6BCF\u4E2A\u5BBF\u4E3B\u673A\u5C31\u662F\u901A\u4FE1\u94FE\u8DEF\u4E2D\u7684\u7F51\u5173\u3002</p><p>\u548C\u5176\u4ED6\u4E24\u79CD\u6A21\u5F0F\u76F8\u6BD4\uFF0Chost-gw \u6A21\u5F0F\u5C11\u4E86\u989D\u5916\u7684\u5C01\u5305\u548C\u62C6\u5305\u8FC7\u7A0B\uFF0C\u6548\u7387\u4E0E\u865A\u62DF\u673A\u76F4\u63A5\u7684\u901A\u4FE1\u76F8\u5DEE\u65E0\u51E0\u3002\u4F46\u662F\uFF0C\u8BE5\u6A21\u5F0F\u8981\u6C42\u6240\u6709\u8282\u70B9\u90FD\u5728\u7269\u7406\u4E8C\u5C42\u7F51\u7EDC\u4E2D\u8054\u901A\uFF0C\u4E14\u6BCF\u4E2A\u4E3B\u673A\u90FD\u9700\u8981\u7EF4\u62A4\u8DEF\u7531\u8868\uFF0C\u8282\u70B9\u89C4\u6A21\u8F83\u5927\u65F6\u6709\u8F83\u5927\u7684\u7EF4\u62A4\u538B\u529B\uFF0C\u56E0\u6B64\u4E0D\u9002\u7528\u590D\u6742\u7F51\u7EDC\u3002</p><h2 id="calico-\u4E3A\u4EC0\u4E48\u6548\u7387\u6BD4-flannel-\u9AD8" tabindex="-1"><a class="header-anchor" href="#calico-\u4E3A\u4EC0\u4E48\u6548\u7387\u6BD4-flannel-\u9AD8" aria-hidden="true">#</a> calico \u4E3A\u4EC0\u4E48\u6548\u7387\u6BD4 flannel \u9AD8</h2><p>\u6574\u4E2A\u8FC7\u7A0B\u4E2D\u59CB\u7EC8\u90FD\u662F\u6839\u636Eiptables\u89C4\u5219\u8FDB\u884C\u8DEF\u7531\u8F6C\u53D1\uFF0C\u5E76\u6CA1\u6709\u8FDB\u884C\u5C01\u5305\uFF0C\u89E3\u5305\u7684\u8FC7\u7A0B\uFF0C\u8FD9\u548Cflannel\u6BD4\u8D77\u6765\u6548\u7387\u5C31\u4F1A\u5FEB\u591A\u4E86\u3002</p><h2 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a> \u53C2\u8003\u94FE\u63A5</h2>',39),p={href:"https://www.kubernetes.org.cn/6908.html",target:"_blank",rel:"noopener noreferrer"},f=a("\u4ECE\u96F6\u5F00\u59CB\u5165\u95E8 K8s | \u7406\u89E3 CNI \u548C CNI \u63D2\u4EF6"),g={href:"https://www.infoq.cn/article/rnbqhui1wipzj6bjiwet",target:"_blank",rel:"noopener noreferrer"},u=a("\u5341\u5206\u949F\u6F2B\u8C08\u5BB9\u5668\u7F51\u7EDC\u65B9\u6848 01\u2014Flannel"),N={href:"https://zhuanlan.zhihu.com/p/161425392",target:"_blank",rel:"noopener noreferrer"},_=a("Flannel\u7F51\u7EDC\u6A21\u578B"),I={href:"https://kuboard.cn/learning/k8s-intermediate/service/cni.html",target:"_blank",rel:"noopener noreferrer"},P=a("\u5982\u4F55\u9009\u62E9\u7F51\u7EDC\u63D2\u4EF6"),A={href:"https://zhuanlan.zhihu.com/p/225063539",target:"_blank",rel:"noopener noreferrer"},V=a("\u5BB9\u5668\u901A\u4FE1flannel\u548Ccalico\u5BF9\u6BD4");function b(w,C){const e=r("ExternalLinkIcon");return h(),t(o,null,[d,l("ul",null,[l("li",null,[l("a",p,[f,n(e)])]),l("li",null,[l("a",g,[u,n(e)])]),l("li",null,[l("a",N,[_,n(e)])]),l("li",null,[l("a",I,[P,n(e)])]),l("li",null,[l("a",A,[V,n(e)])])])],64)}var m=i(c,[["render",b],["__file","cni.html.vue"]]);export{m as default};
