import{_ as l,r as i,c as o,b as e,a as c,F as r,d as a,e as t,o as s}from"./app.e7d2e5c5.js";const d={},p=a("\u8F6C\u8F7D\u81EA\uFF1A "),h={href:"https://developer.aliyun.com/learning/course/572/detail/7866?accounttraceid=2703db7b0eea4ec5a775b5aaaca87dd5vtsl",target:"_blank",rel:"noopener noreferrer"},g=a("\u7406\u89E3CNI\u548CCNI\u63D2\u4EF6 | \u963F\u91CC\u4E91\u539F\u751F"),b=t('<h1 id="cni" tabindex="-1"><a class="header-anchor" href="#cni" aria-hidden="true">#</a> CNI</h1><h2 id="cni-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#cni-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> CNI \u662F\u4EC0\u4E48</h2><p>CNI\uFF0C\u5B83\u7684\u5168\u79F0\u662F Container Network Interface\uFF0C\u5373\u5BB9\u5668\u7F51\u7EDC\u7684 API \u63A5\u53E3\u3002</p><p>\u5B83\u662F K8s \u4E2D\u6807\u51C6\u7684\u4E00\u4E2A\u8C03\u7528\u7F51\u7EDC\u5B9E\u73B0\u7684\u63A5\u53E3\u3002Kubelet \u901A\u8FC7\u8FD9\u4E2A\u6807\u51C6\u7684 API \u6765\u8C03\u7528\u4E0D\u540C\u7684\u7F51\u7EDC\u63D2\u4EF6\u4EE5\u5B9E\u73B0\u4E0D\u540C\u7684\u7F51\u7EDC\u914D\u7F6E\u65B9\u5F0F\u3002</p><h2 id="k8s-\u4E2D\u5982\u4F55\u4F7F\u7528cni" tabindex="-1"><a class="header-anchor" href="#k8s-\u4E2D\u5982\u4F55\u4F7F\u7528cni" aria-hidden="true">#</a> k8s \u4E2D\u5982\u4F55\u4F7F\u7528CNI</h2><p>K8s \u901A\u8FC7 CNI \u914D\u7F6E\u6587\u4EF6\u6765\u51B3\u5B9A\u4F7F\u7528\u4EC0\u4E48 CNI\u3002</p><p>\u57FA\u672C\u7684\u4F7F\u7528\u65B9\u6CD5\u4E3A\uFF1A</p><ol><li>\u9996\u5148\u5728\u6BCF\u4E2A\u7ED3\u70B9\u4E0A\u914D\u7F6E CNI \u914D\u7F6E\u6587\u4EF6\uFF0C\u5728 /etc/cni/net.d/ \u76EE\u5F55\u4E0B\uFF1B</li><li>\u5B89\u88C5 CNI \u914D\u7F6E\u6587\u4EF6\u4E2D\u6240\u5BF9\u5E94\u7684\u4E8C\u8FDB\u5236\u63D2\u4EF6\u5230 /opt/cni/bin/ \u76EE\u5F55\u4E0B\uFF1B</li><li>\u5728\u8FD9\u4E2A\u8282\u70B9\u4E0A\u521B\u5EFA Pod \u4E4B\u540E\uFF0CKubelet \u5C31\u4F1A\u6839\u636E CNI \u914D\u7F6E\u6587\u4EF6\u6267\u884C\u524D\u4E24\u6B65\u6240\u5B89\u88C5\u7684 CNI \u63D2\u4EF6\uFF1B</li><li>\u4E0A\u6B65\u6267\u884C\u5B8C\u4E4B\u540E\uFF0CPod \u7684\u7F51\u7EDC\u5C31\u914D\u7F6E\u5B8C\u6210\u4E86\u3002</li></ol><p>\u5177\u4F53\u7684\u6D41\u7A0B\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://clay-blog.oss-cn-shanghai.aliyuncs.com/img/1576553351154-fa1f252a-61b8-48b9-a95f-092f9a48392d-20221122160459021.png" alt="img" loading="lazy"></p><p>\u5728\u96C6\u7FA4\u91CC\u9762\u521B\u5EFA\u4E00\u4E2A Pod \u7684\u65F6\u5019\uFF0C\u9996\u5148\u4F1A\u901A\u8FC7 apiserver \u5C06 Pod \u7684\u914D\u7F6E\u5199\u5165\u3002apiserver \u7684\u4E00\u4E9B\u7BA1\u63A7\u7EC4\u4EF6\uFF08\u6BD4\u5982 Scheduler\uFF09\u4F1A\u8C03\u5EA6\u5230\u67D0\u4E2A\u5177\u4F53\u7684\u8282\u70B9\u4E0A\u53BB\u3002Kubelet \u76D1\u542C\u5230\u8FD9\u4E2A Pod \u7684\u521B\u5EFA\u4E4B\u540E\uFF0C\u4F1A\u5728\u672C\u5730\u8FDB\u884C\u4E00\u4E9B\u521B\u5EFA\u7684\u64CD\u4F5C\u3002\u5F53\u6267\u884C\u5230\u521B\u5EFA\u7F51\u7EDC\u8FD9\u4E00\u6B65\u9AA4\u65F6\uFF0C\u9996\u5148\u5B83\u4F1A\u8BFB\u53D6\u521A\u624D\u6211\u4EEC\u6240\u8BF4\u7684\u914D\u7F6E\u76EE\u5F55\u4E2D\u7684\u914D\u7F6E\u6587\u4EF6\uFF0C\u914D\u7F6E\u6587\u4EF6\u91CC\u9762\u4F1A\u58F0\u660E\u6240\u4F7F\u7528\u7684\u662F\u54EA\u4E00\u4E2A\u63D2\u4EF6\uFF0C\u7136\u540E\u53BB\u6267\u884C\u5177\u4F53\u7684 CNI \u63D2\u4EF6\u7684\u4E8C\u8FDB\u5236\u6587\u4EF6\uFF0C\u518D\u7531 CNI \u63D2\u4EF6\u8FDB\u5165 Pod \u7684\u7F51\u7EDC\u7A7A\u95F4\u53BB\u914D\u7F6E Pod \u7684\u7F51\u7EDC\u3002\u914D\u7F6E\u5B8C\u6210\u4E4B\u540E\uFF0CKuberlet \u4E5F\u5C31\u5B8C\u6210\u4E86\u6574\u4E2A Pod \u7684\u521B\u5EFA\u8FC7\u7A0B\uFF0C\u8FD9\u4E2A Pod \u5C31\u5728\u7EBF\u4E86\u3002</p><h2 id="\u54EA\u4E2A-cni-\u63D2\u4EF6\u9002\u5408\u6211" tabindex="-1"><a class="header-anchor" href="#\u54EA\u4E2A-cni-\u63D2\u4EF6\u9002\u5408\u6211" aria-hidden="true">#</a> \u54EA\u4E2A CNI \u63D2\u4EF6\u9002\u5408\u6211</h2><p>\u901A\u5E38\u6765\u8BF4\uFF0CCNI \u63D2\u4EF6\u53EF\u4EE5\u5206\u4E3A\u4E09\u79CD\uFF1AOverlay\u3001\u8DEF\u7531\u53CA Underlay\u3002</p><p>\u5E38\u7528\u63D2\u4EF6\u6A21\u5F0F\u5206\u7C7B\uFF1A</p><ul><li>overlay\uFF1Aflannel vxlan\u3001calico ipip</li><li>\u8DEF\u7531\u6A21\u5F0F\uFF1Acalico bpg\u3001flannel host-gw</li></ul><p><img src="https://clay-blog.oss-cn-shanghai.aliyuncs.com/img/1576553351147-0fabc723-8d47-4c85-98ab-1da2f88c5fd1-20221122155052943-20221122160459692.png" alt="img" loading="lazy"></p><ul><li><p><strong>Overlay \u6A21\u5F0F</strong>\u7684\u5178\u578B\u7279\u5F81\u662F\u5BB9\u5668\u72EC\u7ACB\u4E8E\u4E3B\u673A\u7684 IP \u6BB5\uFF0C\u8FD9\u4E2A IP \u6BB5\u8FDB\u884C\u8DE8\u4E3B\u673A\u7F51\u7EDC\u901A\u4FE1\u65F6\u662F\u901A\u8FC7\u5728\u4E3B\u673A\u4E4B\u95F4\u521B\u5EFA\u96A7\u9053\u7684\u65B9\u5F0F\uFF0C\u5C06\u6574\u4E2A\u5BB9\u5668\u7F51\u6BB5\u7684\u5305\u5168\u90FD\u5C01\u88C5\u6210\u5E95\u5C42\u7684\u7269\u7406\u7F51\u7EDC\u4E2D\u4E3B\u673A\u4E4B\u95F4\u7684\u5305\u3002\u8BE5\u65B9\u5F0F\u7684\u597D\u5904\u5728\u4E8E\u5B83\u4E0D\u4F9D\u8D56\u4E8E\u5E95\u5C42\u7F51\u7EDC\uFF1B</p></li><li><p><strong>\u8DEF\u7531\u6A21\u5F0F</strong>\u4E2D\u4E3B\u673A\u548C\u5BB9\u5668\u4E5F\u5206\u5C5E\u4E0D\u540C\u7684\u7F51\u6BB5\uFF0C\u5B83\u4E0E Overlay \u6A21\u5F0F\u7684\u4E3B\u8981\u533A\u522B\u5728\u4E8E\u5B83\u7684\u8DE8\u4E3B\u673A\u901A\u4FE1\u662F\u901A\u8FC7\u8DEF\u7531\u6253\u901A\uFF0C\u65E0\u9700\u5728\u4E0D\u540C\u4E3B\u673A\u4E4B\u95F4\u505A\u4E00\u4E2A\u96A7\u9053\u5C01\u5305\u3002\u4F46\u8DEF\u7531\u6253\u901A\u5C31\u9700\u8981\u90E8\u5206\u4F9D\u8D56\u4E8E\u5E95\u5C42\u7F51\u7EDC\uFF0C\u6BD4\u5982\u8BF4\u8981\u6C42\u5E95\u5C42\u7F51\u7EDC\u6709\u4E8C\u5C42\u53EF\u8FBE\u7684\u4E00\u4E2A\u80FD\u529B\uFF1B</p></li><li><p><strong>Underlay \u6A21\u5F0F</strong>\u4E2D\u5BB9\u5668\u548C\u5BBF\u4E3B\u673A\u4F4D\u4E8E\u540C\u4E00\u5C42\u7F51\u7EDC\uFF0C\u4E24\u8005\u62E5\u6709\u76F8\u540C\u7684\u5730\u4F4D\u3002\u5BB9\u5668\u4E4B\u95F4\u7F51\u7EDC\u7684\u6253\u901A\u4E3B\u8981\u4F9D\u9760\u4E8E\u5E95\u5C42\u7F51\u7EDC\u3002\u56E0\u6B64\u8BE5\u6A21\u5F0F\u662F\u5F3A\u4F9D\u8D56\u4E8E\u5E95\u5C42\u80FD\u529B\u7684</p></li></ul><blockquote><p>\u4E13\u5BB6\u8BF4\uFF1A\u8DEF\u7531\u6A21\u5F0F\u5176\u5B9E\u5C5E\u4E8E Underlay \u6A21\u5F0F\u7684\u4E00\u79CD\u7279\u4F8B</p></blockquote><p><img src="https://clay-blog.oss-cn-shanghai.aliyuncs.com/img/1576553351164-abd9e024-8ce1-4f07-8f79-1ab2b2e4b989-20221122160500229.png" alt="img" loading="lazy"></p>',19);function u(f,I){const n=i("ExternalLinkIcon");return s(),o(r,null,[e("blockquote",null,[e("p",null,[p,e("a",h,[g,c(n)])])]),b],64)}var m=l(d,[["render",u],["__file","cni.html.vue"]]);export{m as default};
