import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as l,e as r}from"./app-10bfa633.js";const a={},s=r('<h1 id="kubernetes-概述" tabindex="-1"><a class="header-anchor" href="#kubernetes-概述" aria-hidden="true">#</a> Kubernetes 概述</h1><h1 id="什么是-kubernetes" tabindex="-1"><a class="header-anchor" href="#什么是-kubernetes" aria-hidden="true">#</a> 什么是 kubernetes</h1><p>容器编排、调度平台，是一个完备的分布式系统支撑平台。</p><h2 id="kubernetes-架构" tabindex="-1"><a class="header-anchor" href="#kubernetes-架构" aria-hidden="true">#</a> kubernetes 架构</h2><p><img src="https://clay-blog.oss-cn-shanghai.aliyuncs.com/img/k8s-archeitecture.png" alt=""></p><ul><li>etcd 兼具一致性和高可用性的键值数据库，只有API Server能与其通信；</li><li>apiserver 提供了资源操作的唯一入口，并提供认证、授权、访问控制、API 注册和发现等机制；</li><li>controller manager 负责维护集群的状态，比如故障检测、自动扩展、滚动更新等；</li><li>scheduler 负责资源的调度，按照预定的调度策略将 Pod 调度到相应的机器上；</li><li>kubelet 负责维护容器的生命周期，同时也负责 Volume（CSI）和网络（CNI）的管理；</li><li>container runtime 负责镜像管理以及 Pod 和容器的真正运行（CRI）；</li></ul><h2 id="k8s-vs-kvm" tabindex="-1"><a class="header-anchor" href="#k8s-vs-kvm" aria-hidden="true">#</a> K8s vs KVM</h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;">Kubernetes</th><th style="text-align:left;">KVM</th></tr></thead><tbody><tr><td style="text-align:left;">效率</td><td style="text-align:left;">从资源声明周期管理和发布流程看，使用容器效率较高</td><td style="text-align:left;">较低</td></tr><tr><td style="text-align:left;">自修复能力</td><td style="text-align:left;">通过<strong>声明式语法</strong>提供了强大的自修复能力 自动保持应用状态、自动健康检查、<strong>自动异常重启</strong>、<strong>自动缩放</strong>、<strong>自动故障迁移</strong></td><td style="text-align:left;">无，需额外软件介入或人工处理</td></tr><tr><td style="text-align:left;">负载均衡</td><td style="text-align:left;">透明的服务注册和服务发现机制、内建智能负载均衡器</td><td style="text-align:left;">不支持应用和LB 动态联动</td></tr><tr><td style="text-align:left;">使用习惯</td><td style="text-align:left;">新技术，需要一定学习成本</td><td style="text-align:left;">方便</td></tr></tbody></table>',8),n=[s];function i(d,h){return e(),l("div",null,n)}const g=t(a,[["render",i],["__file","kubernetes-overview.html.vue"]]);export{g as default};
